ROOT_SCREENSHOT_PATH = Path(__file__).parent
ABIS_FOLDER = "%s/abis" % (os.path.dirname(__file__))

PLUGIN_NAME = get_appname_from_makefile()

class TestTRX():
    '''Test TRX client.'''

    def configuration(self, backend: BackendInterface, navigator, firmware):
        if type(backend) is SpeculosBackend:
            if firmware.device == "flex":
                instructions = [
                    # Go to settings menu.
                    NavIns(NavInsID.USE_CASE_HOME_SETTINGS),
                    # Allow data in TXs
                    NavIns(NavInsID.TOUCH, (200, 150)),
                    # Allow custom contracts
                    NavIns(NavInsID.TOUCH, (200, 300)),
                    NavIns(NavInsID.USE_CASE_SETTINGS_NEXT),
                    # Allow sign by hash
                    NavIns(NavInsID.TOUCH, (200, 150)),
                    # Go back to main menu.
                    NavIns(NavInsID.USE_CASE_SETTINGS_MULTI_PAGE_EXIT),
                ]
            elif firmware.device == "stax":
                instructions = [
                    # Go to settings menu.
                    NavIns(NavInsID.USE_CASE_HOME_SETTINGS),
                    # Allow data in TXs
                    NavIns(NavInsID.TOUCH, (200, 150)),
                    # Allow custom contracts
                    NavIns(NavInsID.TOUCH, (200, 300)),
                    # Allow sign by hash
                    NavIns(NavInsID.TOUCH, (200, 450)),
                    # Go back to main menu.
                    NavIns(NavInsID.USE_CASE_SETTINGS_MULTI_PAGE_EXIT),
                ]
            else:
                instructions = [
                    # Go to settings main menu
                    NavInsID.RIGHT_CLICK,
                    NavInsID.RIGHT_CLICK,
                    NavInsID.BOTH_CLICK,
                    # Allow data in TXs
                    NavInsID.BOTH_CLICK,
                    # Allow custom contracts
                    NavInsID.RIGHT_CLICK,
                    NavInsID.BOTH_CLICK,
                    # Allow sign by hash
                    NavInsID.RIGHT_CLICK,
                    NavInsID.RIGHT_CLICK,
                    NavInsID.BOTH_CLICK,
                ]
                if firmware.device != "nanos":
                    instructions += [NavInsID.RIGHT_CLICK]
                instructions += [
                    # Go back to main menu
                    NavInsID.RIGHT_CLICK,
                    NavInsID.BOTH_CLICK
                ]

            navigator.navigate(instructions,
                            screen_change_before_first_instruction=False)

    def clear_sign_and_validate(self, client, firmware, text_index, tx, signatures=[], navigate=True, warning_approve=False):
        path = Path(currentframe().f_back.f_code.co_name)
        text = None
        if firmware.device.startswith("nano"):
            if text_index == 0:
                text = "Sign"
            elif text_index == 1:
                text = "Accept"
        else:
            if text_index == 0 or text_index == 1:
                text = "Hold to sign"
        assert text
        resp = client.clear_sign(client.getAccount(0)['path'],
                            tx,
                            signatures=signatures,
                            snappath=path,
                            text=text,
                            navigate=navigate,
                            warning_approve=warning_approve)
        assert check_tx_signature(tx, resp.data[0:65],
                                    client.getAccount(0)['publicKey'][2:])

